'use strict';const express=require("express"),app=express(),router=express.Router(),Category=require("../../../models/Category"),faker=require("faker"),{userAuth}=require("../../../helpers/authenticate");router.all("/*",(b,c,a)=>{b.app.locals.layout="admin";a()});router.get("/",(b,c)=>{Category.find().then(a=>{c.render("accounts/admin/category",{categories:a})}).catch(a=>console.log(a))});
router.post("/create",(b,c)=>{const a=new Category;a.title=b.body.title;a.date=new Date;a.save().then(d=>{b.flash("success_msg",`${d.title} created successfully :)`);c.redirect("/admin/categories")}).catch(d=>console.log(d))});router.post("/dummy",(b,c)=>{for(let a=0;a<b.body.number;a++){const d=new Category;d.title=faker.lorem.word();d.date=new Date;d.save().then(e=>{b.flash("success_msg",`${b.body.number} dummy categories successfully created  :)`);c.redirect("/admin/categories")}).catch(e=>console.log(e))}});
