'use strict';const express=require("express"),app=express(),router=express.Router(),faker=require("faker"),Contact=require("../../../models/Contact");router.all("/*",(b,c,a)=>{b.app.locals.layout="user";a()});router.get("/",(b,c)=>{Contact.find({user:b.session.user}).populate("user").then(a=>{c.render("accounts/user/contact",{contacts:a})}).catch(a=>console.log(a))});router.get("/create",(b,c)=>{c.render("accounts/user/contact/create")});
