'use strict';const express=require("express"),app=express(),User=require("../../models/User"),bcrypt=require("bcryptjs"),router=express.Router();router.all("/*",(a,c,b)=>{a.app.locals.layout="logs";b()});router.get("/sign",(a,c)=>{c.render("home/logs")});
router.post("/login",(a,c)=>{User.findOne({email:a.body.email}).then(b=>{b?bcrypt.compare(a.body.password,b.password,(d,e)=>{d&&console.log(d);e?"Admin"===b.role?console.log("User logged in as a Admin"):console.log("User logged in as a Subscriber"):(a.flash("error_msg","Password mismatch. Try again ): "),c.redirect("back"))}):(a.flash("error_msg","Email not recognised. Try again ): "),c.redirect("back"))})});
router.post("/register",(a,c)=>{User.findOne({email:a.body.email}).then(b=>{if(b)a.flash("error_msg","User exists with email ):"),c.redirect("back");else{const d=new User;d.email=a.body.email;d.phone=a.body.phone;d.password=a.body.password;bcrypt.genSalt(10,(e,h)=>{bcrypt.hash(a.body.password,h,(f,k)=>{f&&console.log(f);d.password=k;d.save().then(g=>{a.flash("success_msg","Account created. Sign in buddy :) ");c.redirect("back")}).catch(g=>console.log(g))})})}}).catch(b=>console.log(b))});
module.exports=router;